<template>
	<div class="course">
		<div class="container clearfix">
			<ul class="coursebox">
			  <li v-for="(item,index) in catgorylist" :key='item.id' 
			  :class="{active:index===currentIndex}" @click="categoryClick(index,item.id)">
			  	{{item.name}}
			  </li>
			
			</ul>

			<div class="courseList" >
				<div class="detail" v-for="(course,index) in courseDetail" :key='course.id' @click="detailHandler(course.id)">
					<div class="head">
						<img :src="course.course_img" alt="" class="backImg">
						<b class="mask" :style = '{background:course.bgColor}'></b>
						<p>{{course.name}}</p>
					</div>
					<div class="content">
						<p>{{course.brief}}</p>
						<div class="content-detail">
							<div>
								<img src="data:image/svg+xml;base64," alt="">
							         <span>{{course.learn_number}}</span>
							         <span>初级</span>
							         <span class="span3" v-if="course.is_promotion">
							         	  <span class="s">{{course.promotion_price}}</span>
							            <span  class="t">{{course.promotion_name}}</span>
							      </span>
							      <span class="span4" v-else>{{course.price}}</span>
								
							</div>
							
						</div>
					</div>
				</div>
			</div>
		</div>
		
		
	</div>
</template>

<script>
export default {

  name: 'Course',
  data(){
	return{
		catgorylist:[],
		currentIndex:0,
		categoryId:0,
		courseDetail:[],
		bgColors:['#4AB2BF','#1895C6','#4C87E0','#A361D9','#F7AE6A',
		'#FF14A0','#61F0E1','#6282A7','#27998E','#3C74CC','#A463DA',
		'#F0A257','#DD4B7A','#59C6BD','#617FA1','#1B92C3','#30A297',
		'#3B73CB','#9E57CA','#A463DA','#1895C6','#A361D9','#FF14A0']
		
	}  
  },
  methods:{
	  detailHandler(id){
		  this.$router.push({
			  name:'course.detail',
			  params:{
				  detailId:id, 
			  }
		  })
	  },
	  categoryClick(index,id){
		  
		  this.currentIndex = index;
		  this.categoryId = id;
		  this.getAllCategoryList();
	  },
	  getcategoryList(){
		this.$http.categoryList()
		.then(res=>{
			if (!res.data.error_no){
				this.catgorylist = res.data.data;
				let catgory = {
					id:0,
					category:0,
					name:'全部'
				}
				this.catgorylist.unshift(catgory);
			}
		})
		  .catch(err=>{
		  	console.log(err)
		  })
	  },
	  
	  // 获取全部的课程列表
  	getAllCategoryList(){
  		this.$http.allCategoryList(this.categoryId)
		
	  	.then(res=>{
	  		if (!res.error_no) {
	  			this.courseDetail = res.data;
				this.courseDetail.forEach((item,index)=>{
	  				this.bgColors.forEach((bgColor,i)=>{
	  					if (i===index) {
	  						item.bgColor = bgColor;
	  					}
	  				})
	  			})
	  			
	  		}
	  	})
	  	.catch(err=>{
	  		console.log(err);
	  	})
  	}
  },
  created() {
	  this.getcategoryList();
	  this.getAllCategoryList();
	
	},
};
</script>

